import UserContext from "../../UserContext"
import { useContext } from "react"
import axios from "axios"
import VoltarParaLogin from "./VoltarParaLogin"

export function CadastrarDadosUser(body){
    console.log("entrou no cadastrar")
    // const { infoCadastro } = useContext(UserContext)
    console.log(body)

    const POST_URL = 'localhost:5000/cadastrar'
    const promiseCadastrar = axios(POST_URL, body)

    promiseCadastrar.then(()=>VoltarParaLogin)
    promiseCadastrar.catch(()=>alert(`Erro ao cadastrar o usário.`))



}
