import axios from "axios"
import { useContext, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import styled from 'styled-components';
import UserContext from '../UserContext'
import { GerarTransacoes } from "./GerarTransacoes.js";

export default function TelaInicial(){

    const userLocalSerializada = localStorage.getItem("user")
    const userLocal = JSON.parse(userLocalSerializada)

    const { token, nome} = userLocal

    const navigate = useNavigate()

    const header = {
        Authorization: `Bearer ${token}`
    }

    function IrParaNovaEntrada(){
        navigate("/entrada")
    }

    function IrParaNovaSaida(){
        navigate("/saida")
    }

    return(
       <ContainerPrincipal>
           <Cabecalho>
           <h1>Olá, {nome}</h1>
           </Cabecalho>
           < GerarTransacoes/>
           <ContainerButton>
           <Adicionar onClick={IrParaNovaEntrada()}><div>
           <ion-icon name="add-circle-outline"></ion-icon>
               <h3>Nova Entrada</h3> 
               </div></Adicionar>
           <Adicionar onClick={IrParaNovaSaida()}>
           <div>
           <ion-icon name="remove-circle-outline"></ion-icon>
               <h3>Nova Saída</h3>
               </div>
           </Adicionar>
           </ContainerButton>

       </ContainerPrincipal>
    )
}

const ContainerPrincipal = styled.div`
    width:100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #8C11BE;

h1{
    font-family: 'Raleway', sans-serif;;
    font-style: normal;
    font-weight: 400;
    font-size: 32px;
    line-height: 50px;
    color: #FFFFFF;
    margin-bottom:24px;
}
`
const Cabecalho = styled.div`
width:100vw;
height: 100vh;
display: flex;
flex-direction: row;
justify-content: space-around; `


const ContainerButton = styled.div`
width:100vw;
height: 25vh;
display: flex;
flex-direction: row;
justify-content: space-around
padding-bottom:16px`

const Adicionar = styled.button`
    width:40vw;
    height: 25vh;
    display: flex;
    background: #A328D6;
    border-radius: 5px;
    border: 0px;
    font-family: 'Raleway';
    font-style: normal;
    font-weight: 700;
    font-size: 17px;
    line-height: 20px;
    color: #FFFFFF;

ion-icon{
    font-size:18px
}`
