import axios from "axios"
import { useContext, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import styled from 'styled-components';
import UserContext from '../UserContext'
import { GerarTransacoes } from "./GerarTransacoes.js";

export default function TelaInicial(){

    const userLocalSerializada = localStorage.getItem("user")
    const userLocal = JSON.parse(userLocalSerializada)

    const { token, nome} = userLocal

    console.log(token)

    const header = {
        Authorization: `Bearer ${token}`
    }

    return(
       <ContainerPrincipal>
           <Cabecalho>
           <h1>Olá, {nome}</h1>
           </Cabecalho>
           < GerarTransacoes/>
           <div>
           <button onClick={IrParaNovaEntrada()}><div>
           <ion-icon name="add-circle-outline"></ion-icon>
               <h3>Nova<br>Entrada</br></h3>
               </div></button>
           <button  onClick={IrParaNovaSaida()}>
           <div>
           <ion-icon name="remove-circle-outline"></ion-icon>
               <h3>Nova<br>Saída</br></h3>
               </div>

           </button>
           </div>

       </ContainerPrincipal>
    )
}

const ContainerPrincipal = styled.div`
    width:100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #8C11BE;

h1{
    font-family: 'Raleway', sans-serif;;
    font-style: normal;
    font-weight: 400;
    font-size: 32px;
    line-height: 50px;
    color: #FFFFFF;
    margin-bottom:24px;
}
`
const Cabecalho = styled.div`
width:100vw;
height: 100vh;
display: flex;
flex-direction: row;
justify-content: space-around; `
