import { useEffect, useState } from "react"

export function gerarTransacoes(props) {
    const userLocalSerializada = localStorage.getItem("user")
    const userLocal = JSON.parse(userLocalSerializada)

    const [itemLista, setItemLista] = useState([true])
    const [listaTransacoes, setListaTransacoes] = useState([])

    const { token, nome } = userLocal

    const header = {
        Authorization: `Bearer ${token}`
    }

    const URLAPI = 'http://localhost:5000/transacoes'

    useEffect(() => {
        const promiseGet = axios.get(URLAPI, header)
        promiseGet.then((response) => setListaTransacoes(response.data))
        promiseGet.catch((response) => alert(`Código do erro${response.error}`))
    }, [excluirTransacao()])


    function excluirTransacao(id_transacao) {
        const body = {
            id_transacao
        }
        const promiseDelete = axios.delete(URLAPI, id_transacao, header)

    }
    return (

        <ContainerTransacoes>
            {listaTransacoes.map(({ data, descricao, valor, id_transacao }) =>
                <div>
                    <h4>{data}</h4>
                    <h3>{descricao}</h3>
                    <h3>{valor}</h3>
                    <button onClick={(id_transacao) => excluirTransacao(id_transacao)}><ion-icon name="close"></ion-icon></button>
                </div>
            )}
        </ContainerTransacoes>
    )
}

const ContainerTransacoes = styled.div`
    width:100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #8C11BE;`
