import axios from "axios"
import { useContext, useEffect, useState } from "react"
import { Navigate, useNavigate } from "react-router-dom"
import styled from 'styled-components';
import UserContext from '../UserContext'

export default function TelaNovaEntrada() {

    const [valor, setValor] = useState()
    const [descricao, setDescricao] = useState()

    const navigate = useNavigate()

    const POST_URL = 'http://localhost:5000/adicionar'

    const userLocalSerializada = localStorage.getItem("user")
    const userLocal = JSON.parse(userLocalSerializada)
    const { token } = userLocal

    const autorizacao = {
        headers: {
            Authorization: `Bearer ${token}`
        }
    }


    function AdicionarCredito(event) {
        event.preventDefault();

        const body={
            descricao: descricao,
            valor: valor,
            tipo: false
        }

        const promisePostCredito = axios.post(POST_URL, body, autorizacao)
        promisePostCredito.then(()=>navigate("/mywallet"))
    }
    return (
        <ContainerPrincipal>
            <Cabecalho>
                <h1>Nova entrada</h1>
            </Cabecalho>
            <form>
                <input placeholder="Valor" onChange={(e) => setValor(e.target.value)}></input>
                <input placeholder="Descrição" onChange={(e) => setDescricao(e.target.value)}></input>
                <Botao type="submit" onClick={AdicionarCredito}>
                    Salvar entrada
                </Botao>
            </form>
        </ContainerPrincipal>
    )
}

const ContainerPrincipal = styled.div`
    width:100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #8C11BE;

h1{
    font-family: 'Raleway', sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 32px;
    line-height: 50px;
    color: #FFFFFF;
}
`
const Cabecalho = styled.div`
width:100vw;
display: flex;
flex-direction: row;
justify-content: space-around; `




const Botao = styled.div`

    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-content: flex-start;
    text-align: left;
    padding-top:12px;
    padding-left: 12px;
    ion-icon{
        font-size:24px


`
