import { useEffect, useState } from "react"
import axios from "axios"
import styled from "styled-components"

export function GerarTransacoes() {
    const userLocalSerializada = localStorage.getItem("user")
    const userLocal = JSON.parse(userLocalSerializada)

    const [itemLista, setItemLista] = useState([true])
    const [listaTransacoes, setListaTransacoes] = useState([])
    const [itemDeletado, setItemDeletado] = useState()

    const { token, nome } = userLocal

    const autorizacao = {
        headers: {
            Authorization: `Bearer ${token}`
        }
    }

    console.log(autorizacao)

    const URLAPI = 'http://localhost:5000/mywallet'

    useEffect(() => {
        const promiseGet = axios.get(URLAPI,{headers: {
            Authorization: `Bearer ${token}`
        }})
        promiseGet.then((response) => setListaTransacoes(response.data))
        promiseGet.catch((response) => alert(`Código do erro${response.error}`))
    }, [excluirTransacao])


    function excluirTransacao(element) {

        setItemDeletado(element.id_transacao)

        const promiseDelete = axios.delete(`${URLAPI}/${element.id_transacao}`,autorizacao)
        promiseDelete.then(()=>console.log("tudo"))
        promiseDelete.catch(()=>alert("Não foi possivel cancelar a sua transação :/"))

    }
    return (

        <ContainerTransacoes>
            {listaTransacoes.map((element) =>
                <div>
                    <Tempo>{element.tempo}</Tempo>
                    <Descricao>{element.descricao}</Descricao>
                    <Valor debito={element.tipo}>{element.valor}</Valor>
                    <button onClick={()=> excluirTransacao(element)}><ion-icon name="close"></ion-icon></button>
                </div>
            )}
        </ContainerTransacoes>
    )
}

const ContainerTransacoes = styled.div`
    padding:22px 24px 0px 24px;
    width:75vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    background: #FFFFFF;
    border-radius: 5px;

    div{
        display: flex;
        flex-direction: row;
        justify-content: space-around;
    }`

    const Tempo = styled.h4`
    width: 10vw;
    font-family: 'Raleway', sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    color: #C6C6C6;`

const Descricao = styled.h3`
width: 60vw;
    font-family: 'Raleway', sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    color: #000000;`

const Valor = styled.h3`
width: 15vw;
    font-family: 'Raleway', sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    text-align: right;
    color: ${(props)=>props.debito=== true? ` #C70000`: `#03AC00`}`
